        internal void draw()
        {
            foreach (Layer layer in layers)
            {
                aux = 0;
                for (int y = 0; y < Config.tilesPerChunk; y++)
                {
                    for (int x = 0; x < Config.tilesPerChunk; x++)
                    {

                        if ((region.X * Config.chunkSize * Config.scale.X) + (x * Config.tileSize * Config.scale.X) <= Game1.display.width   - Game1.matrix.X + Game1.display.translate.X &&
                            (region.X * Config.chunkSize * Config.scale.X) + (x * Config.tileSize * Config.scale.X) >= 0                     - Game1.matrix.X - Game1.display.translate.X - (Config.tileSize * Config.scale.X) &&
                            (region.Y * Config.chunkSize * Config.scale.Y) - (y * Config.tileSize * Config.scale.Y) <= 0                     - Game1.matrix.Y + Game1.display.translate.Y + (Config.tileSize * Config.scale.Y) &&
                            (region.Y * Config.chunkSize * Config.scale.Y) - (y * Config.tileSize * Config.scale.Y) >= -Game1.display.height - Game1.matrix.Y - Game1.display.translate.Y)
                        for (int a = 1; a < auxList.Count; a++)
                        {
                            if (layer.data[aux] > auxList[a - 1] && layer.data[aux] <= auxList[a] && auxList[a] > 0)
                            {
                                tileset = Game1.race.tilesets[chunkTilesets.ElementAt(a - 1)];
                                numX = (layer.data[aux] - auxList[a - 1]) % tileset.tilesPerRow;
                                numY = (layer.data[aux] - auxList[a - 1]) / tileset.tilesPerRow;

                                if (numX == 0)
                                {
                                    numY--;
                                    numX = tileset.tilesPerRow - 1;
                                }
                                else
                                {
                                    numX--;
                                }

                                tileset.drawTile((int)((region.X * Config.chunkSize * Config.scale.X) + (x * Config.tileSize * Config.scale.X)),
                                                 (int)((region.Y * Config.chunkSize * Config.scale.Y) - (y * Config.tileSize * Config.scale.Y)), numX, numY, Color.White);
                                break;
                            }
                        }
                        aux++;
                    }
                }
            }
        }
    }
